INSERT INTO `project` VALUES (15, '黑森森头皮深层清洁', 0, 3, 8, 0, '祛除头发油脂污垢疏通毛孔使头皮保持清爽净透和秀发柔顺亮丽', 'hss_project/2019081311214842293.jpeg', 'hss_project/1565666494929597.jpg', '&lt;p&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;有效袪除头发和头皮的油脂和污垢，疏通堵塞毛孔，净化毛囊，使头皮清爽净透，秀发柔顺亮丽。&lt;/span&gt;&lt;/p&gt;', 1557476984, 1565666509, 1, '头皮清洁,疏通毛囊,拓客', 0, 'hss_project/1565666503840215.jpg,hss_project/1557804264579414.jpg,hss_project/1557804264735590.jpg,hss_project/1557804264907069.jpg', '', '黑森森头皮深层清洁', '', 'hss_project/2019052814564271058.jpeg,hss_project/2019052814564207586.jpeg,hss_project/2019052814564292541.jpeg,hss_project/2019052814564295487.jpeg,hss_project/2019052814564246375.jpeg', '&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;项目名称：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;黑森森头皮深层清洁&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;护理次数：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;7&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:宋体\\&quot;&gt;天／１次&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;项目功效：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;有效舒缓头发和头皮的油脂和净化污垢，疏通堵塞毛孔，通筋活络，使头皮干爽有弹性，秀发柔顺亮丽。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;适用人群：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;适用于所有人群。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;使用仪器：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;互联头皮检测仪、毛囊活力梳&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;护理部位：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;头皮、头发&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;护理时间：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;35&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;分钟&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-size:19px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;注意事项&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;★&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;头部皮肤溃烂者不建议做此护理&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;★&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;体质衰弱，患严重心脏病者不建议做此护理&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;★&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;临产期孕妇不建议做此护理&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\\&quot;;f