INSERT INTO `project` VALUES (18, '黑森森头皮敏感修护', 0, 3, 10, 0, '舒缓头皮发红紧绷瘙痒等症状舒敏消炎滋养头皮增强头皮免疫力', 'hss_project/2019080709344490158.jpeg', 'hss_project/1565085392750243.jpg', '&lt;p style=\\&quot;line-height: 15px;\\&quot;&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;有效舒缓头皮发红、紧绷、麻木、瘙痒等症状，舒敏消炎，滋润头皮，增强头皮免疫力，改善头皮亚健康状态。配合仪器操作，极大提高活性成分吸收及效果成倍呈现。&lt;/span&gt;&lt;/p&gt;', 1557730162, 1565145533, 1, '修护敏感,痘痘头皮,干燥瘙痒', 0, 'hss_project/1565141307630847.jpg,hss_project/1557812814186488.jpg,hss_project/1557812814764762.jpg,hss_project/1557812814116515.jpg', '', '黑森森头皮敏感修护', '', 'hss_project/2019052814194631779.jpeg,hss_project/2019052814194931179.jpeg,hss_project/2019052814195465131.jpeg,hss_project/2019052814195684098.jpeg,hss_project/2019052814195998739.jpeg,hss_project/2019052814200111262.jpeg,hss_project/2019052814200308641.jpeg,hss_project/2019061011163153696.jpeg', '&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;项目名称：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;黑森森头皮敏感修护&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;疗程设置：&lt;/span&gt;&lt;/strong&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;7&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:宋体\\&quot;&gt;天／１次，&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;10&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:宋体\\&quot;&gt;次为&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;1&lt;/span&gt;&lt;span style=\\&quot;font-size:12px;font-family:宋体\\&quot;&gt;个疗程&lt;/s