INSERT INTO `scene` VALUES (65, '锁客方略', 0, 3, 6, 0, 'hss_scene/2019051014553664942.jpeg', 'hss_scene/1557563881571372.jpg', '&lt;p style=\\&quot;margin-bottom: 0px;\\&quot;&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;会员享多项高端项目，&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;不断挖掘潜在顾客，带动更多项目消费，并将其培养成忠实顾客&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;，&lt;/span&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;让经营可持续发展。&lt;/span&gt;&lt;/p&gt;', '黑森森锁客方略：门店会员品质尊享，牢牢锁住门店忠实客户', '会员享多项高端项目，不断挖掘潜在顾客，带动更多项目消费，并将其培养成忠实顾客，让经营可持续发展', 1551246168, 1562132977, 1, '身体SPA,会员专享  ', 1, 'mcs_scheme/2019032015091289593.png', 'temp/2019032015092484930.png', 1, 'detail', 2, '锁客方略•门店会员品质尊享');
INSERT INTO `scene` VALUES (70, '新零售引流', 0, 3, 4, 0, 'hss_scene/2019051015180823678.jpeg', 'hss_scene/1557563932862743.jpg', '&lt;p&gt;&lt;span style=\\&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;\\&quot;&gt;低温微射流技术结合无添加防腐专利技术，高效保留植物活性；源自进口原料原产地，给予肌肤安全、健康呵护。&lt;/span&gt;&lt;/p&gt;', '黑森森新零售引流：精选多款新零售居家产品，引爆暴力裂变', '低温微射流技术结合无添加防腐专利技术，高效保留植物活性；源自进口原料原产地，给予肌肤安全、健康呵护', 1552012863, 1562133038, 1, '引流裂变,社交电商 ', 1, 'mcs_scheme/2019032015114625307.png', 'temp/2019032015114792177.png', 1, 'detail', 4, '新零售引流•暴力裂变方程式');
INSERT INTO `scene` VALUES (71, '新零售热销', 0, 3, 3, 0, 'hss_scene/2019061810434929721.jpeg', 'hss_scene/1557563951396321.jpg', '&lt;p&gt;&lt;span style=\\&quot;font-family: 微软雅黑, sans-serif; font-size: 16px;\\&quot;&gt;黑森森品质精选，产品颜值高、体验优、香氛独特、效果强，品质与创新并重，轻松维持产品超高复购率！&lt;/span&gt;&lt;/p&gt;', '黑森森新零售热销：精选多款当下热销的高颜值强效果网红产品', '黑森森品质精选，产品颜值高、体验优、香氛独特、效果强，品质与创新并重，轻松维持产品超高复购率', 1552012951, 1562133060, 1, '高品质,创新,爆款', 1, 'mcs_scheme/2019032015125335872.png', 'temp/2019032015121803471.png', 1, 'detail', 4, '新零售热销•人气网红尖品');
INSERT INTO `scene` VALUES (72, '爆款居家美容', 0, 3, 2, 0, 'hss_scene/2019051015203701963.jpeg', 'hss_scene/1557563972791098.jpg', '&lt;p&gt;&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;细胞科技护发产品&lt;/span&gt;+&lt;span style=\\&quot;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;居家头皮护理仪器，突破传统居家护肤方式，在家也可以享受院店式专业头皮护理。&lt;/span&gt;&lt;/p&gt;', '黑森森爆款居家美容：颠覆式的居家生活美容和院级护理', '细胞科技护发产品+居家头皮护理仪器，突破传统居家护肤方式，在家也可以享受院店式专业头皮护理', 1552014027, 1562133084, 1, '居家美容,高科技,尖品', 0, 'mcs_scheme/2019032015154752945.png', 'temp/2019032015155036351.png', 1, 'detail', 4, '爆款居家美容•颠覆式居家生活美容');
INSERT INTO `scene` VALUES (73, '玩转新零售', 0, 3, 1, 0, 'hss_scene/2019051015235071893.jpeg', 'hss_scene/1557564012483307.jpg', '&lt;p style=\\&quot;line-height: 15px;\\&quot;&gt;&lt;span style=\\&quot;;font-family:&amp;#39;微软雅黑&amp;#39;,&amp;#39;sans-serif&amp;#39;\\&quot;&gt;源自澳大利亚、英国、日本等原料产地，低温微射流技术结合无添加防腐专利，最大程度保留植物有效活性，给予肌肤安全、健康养护。&lt;/span&gt;&lt;/p&gt;', '黑森森玩转新零售：由头皮到秀发、由头部到身体的健康护理', '精选澳大利亚、英国、日本等产地原料，以专利技术生产，最大程度保留植物天然活性，是新零售单品的不二之选', 1552014090, 1562133110, 1, '', 0, 'mcs_scheme/2019032015290968702.png', 'temp/2019032015290739103.png', 2, 'sort', 5, '黑森森新零售商品分类');
